query getCategoryDetails($category: String!, $nValue: String!, $sl: String!, $locale: String!, $Ns: String, $storeId: String, $pageSize: Int, $page: Int, $onlyStore: Boolean) {
  categoryDetails(
    category: $category
    nValue: $nValue
    locale: $locale
    sl: $sl
    Ns: $Ns
    page: $page
    pageSize: $pageSize
    storeId: $storeId
    onlyStore: $onlyStore
  ) {
    products {
      allAvailableSizes
      currencyCode
      displayName
      listPrice
      parentCategoryUnifiedId
      productSalePrice: salePrice
      pdpUrl
      skuStyleOrder {
        colorName
        size
        sku
      }
      }
  }
}
